//get natural height and width of an image
(function($) { 
	var props = ['Width', 'Height'], prop;
	while (prop = props.pop()) {
		(function (natural, prop) {
			$.fn[natural] = (natural in new Image()) ? 
			function () {
				return this[0][natural];
			} : 
			function () {
				var node = this[0], img, value;

				if (node.tagName.toLowerCase() === 'img') {
					img = new Image();
					img.src = node.src,
					value = img[prop];
				}
				return value;
			};
		}('natural' + prop, prop.toLowerCase()));
	}
}(jQuery));

// Image POI (Points of interest that show on homepage panels)
(function() {

	window.immerse = window.immerse || {};
	window.immerse.imgPoi = (function($) {
		/* make sure that jquery is used */

		var _public = {};

		function poi(coords, title, target, $img) {
			this.title = title;
			this.target = target;
			coords = coords.split(",");
 
	        //only circles are supported
	        if(!coords || coords.length !== 3){
	            return;
	        }

			var nWidth = $img.naturalWidth(), nHeight = $img.naturalHeight(); 

	        //Get percentage of width based on original img size so that it will move left or right as the img scales
	        //Image map component uses img servlet which shows image as width of 1280. Need to compensate for that
	        var offsetWidth = -(nWidth - 1280) > 0 ? -(nWidth - 1280) : (nWidth - 1280);
	        this.left = (parseInt(coords[0]) / (nWidth - offsetWidth)) * 100;

	        //Get percentage of height based on original img size so that it will move up or down as the img scales
	        //Image map component uses img servlet which shows image as height of 909. Need to compensate for that
	        var offsetHeight = -(nHeight - 909) > 0 ? -(nHeight - 909) : (nHeight - 909);
	        this.top = (parseInt(coords[1]) / (nHeight - offsetHeight)) * 100;
	    }

		function getPois($imgContainer, $img) {
	 		// console.log("imgContainer:", $imgContainer);
			var $map = $imgContainer.find('map');
	        var pois = [];
	 
	        if(!$map || ($map.length == 0)){
	            return;
	        }
	 
	        $.each($map.children(), $.proxy(function(i, v){
	            pois.push(new poi(v.coords, v.title, v.target, $img));
	        }, this));
	 
	        return pois;
		}

		function addPois($imgContainer, pois) {
			$.each(pois, function(i, c){
	        	$imgContainer.append($("<div><a href='javascript:void(0);'>more</a><span>" + c.title + "</span></div>")
	        		.addClass("poi").addClass(c.target == '' ? 'left': c.target.toLowerCase()).css("top", c.top + "%").css("left", c.left + "%"));
	        });
		}

		_public.init = function() {
			var headerSelector = '.immerse-story-header div.fullscreen-media';
			var imgSelector = 'img.fullscreen-media';

			$.each($(headerSelector), function(i, header){
				var $header = $(header);
				var $img = $header.find(imgSelector);

				//disable image map
				$img.removeAttr('usemap');

				$img.one("load", function() {

					var headerPois = getPois($header,$img);

					if(!headerPois || headerPois.length ==0){
						return true;
					}
					//Remove existing pois
					$header.find('div.poi').remove();

					//Adds the pois to the image div
					addPois($header.find('.poi-wrap'), headerPois);
				}).each(function() {
					if(this.complete) $(this).load();
				});
				
			});
		}
		return _public;
	})(jQuery);

}).call(this);
